import React, { useState } from "react";

function CreateArea() {

    const [note, setNote] = useState({
        title:"",
        content:""
    });


    return (
        <form>
            <div>
                <input name="title" value={note.title} placeholder="Enter Title" />
                <textarea name="content" value={note.content} placeholder="Enter a Note..." rows="3" />
                <button> Add </button>
            </div>
        </form>
    );
}

export default CreateArea;
